CREATE OR REPLACE FUNCTION obter_ult_unidade_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_unidade_w			varchar(10) := '';

/*
B - BÃ¡sica
C - Complementar
*/
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
begin
if (ie_opcao_p = 'B') then
	select	a.cd_unidade_basica
	into STRICT	cd_unidade_w
	from	 atend_paciente_unidade a
	where	 a.nr_atendimento = nr_atendimento_p
	and		 a.dt_entrada_unidade =
		 (
		  SELECT max(b.dt_entrada_unidade)
		  from	 atend_paciente_unidade b
		  where	 b.nr_atendimento = a.nr_atendimento
		 );
elsif (ie_opcao_p = 'C') then
	select	a.cd_unidade_compl
	into STRICT	cd_unidade_w
	from	 atend_paciente_unidade a
	where	 a.nr_atendimento = nr_atendimento_p
	and		 a.dt_entrada_unidade =
		 (
		  SELECT max(b.dt_entrada_unidade)
		  from	 atend_paciente_unidade b
		  where	 b.nr_atendimento = a.nr_atendimento
		 );
end if;
end;
end if;
return cd_unidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
