SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ult_vl_unit_mat (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;
nr_seq_nf_w			bigint;


BEGIN

vl_retorno_w	:= 0;
nr_seq_nf_w	:= 0;

select	max(nr_sequencia)
into STRICT	nr_seq_nf_w
from	nota_fiscal a,
	natureza_operacao b
where	a.cd_natureza_operacao 	= b.cd_natureza_operacao
and	a.ie_acao_nf 		= '1'
and	a.ie_situacao       		= '1'
and	a.cd_estabelecimento 	= cd_estabelecimento_p
and	b.ie_entrada_saida  	= 'E'
and	exists (SELECT	1
		from	nota_fiscal_item x
		where	x.nr_sequencia	= a.nr_sequencia
		and	x.cd_material	= cd_material_p);

if (nr_seq_nf_w > 0) then
	begin

	select	max(coalesce(vl_unitario_item_nf,0))
	into STRICT	vl_retorno_w
	from	nota_fiscal_item
	where	nr_sequencia = nr_seq_nf_w
	and	cd_material	 = cd_material_p;

	end;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
