SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_um_comum (nr_seq_ficha_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unid_medida_comum_w	varchar(2000) := null;
qt_materiais_w	bigint;
qt_materiais_ww bigint;
cd_unid_medida_w	varchar(30);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;

c01 CURSOR FOR
SELECT 	COUNT(*),
	a.cd_unidade_medida_consumo
FROM (	SELECT 	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30) cd_unidade_medida_consumo,
		cd_material
	FROM	material
	WHERE 	cd_material IN (SELECT	d.cd_material
				FROM	material d
				WHERE	d.nr_seq_ficha_tecnica = nr_seq_ficha_p)
	
UNION

	SELECT	cd_unidade_medida,
			cd_material
	FROM	material_conversao_unidade
	WHERE	cd_material IN (SELECT	d.cd_material
				FROM	material d
				WHERE	d.nr_seq_ficha_tecnica = nr_seq_ficha_p)
	--GROUP BY cd_unidade_medida,
	--		cd_material
	ORDER BY 2
	) a
GROUP BY a.cd_unidade_medida_consumo
having count(*) = qt_materiais_w
ORDER BY 1 DESC;


BEGIN

if (nr_seq_ficha_p IS NOT NULL AND nr_seq_ficha_p::text <> '') then
	begin

	select	count(*)
	into STRICT 	qt_materiais_w
	from 	material
	where 	nr_seq_ficha_tecnica = nr_seq_ficha_p;

	open C01;
	loop
	fetch C01 into
		qt_materiais_ww,
		cd_unid_medida_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		cd_unid_medida_comum_w := cd_unid_medida_comum_w || cd_unid_medida_w || '#0';
		end;
	end loop;
	close C01;

	end;
end if;

return	cd_unid_medida_comum_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_um_comum (nr_seq_ficha_p bigint) FROM PUBLIC;

