CREATE OR REPLACE FUNCTION obter_um_gas ( nr_seq_gas_p bigint, ie_disp_resp_esp_p text) RETURNS varchar AS $body$
DECLARE


ie_unidade_medida_w	varchar(50);


BEGIN

select	max(ie_unidade_medida)
into STRICT	ie_unidade_medida_w
from	regra_unidade_gas
where	nr_seq_gas		= nr_seq_gas_p
and	ie_disp_resp_esp	= ie_disp_resp_esp_p;

return	ie_unidade_medida_w;

end;
