CREATE OR REPLACE FUNCTION obter_um_medic_conta ( nr_unidade_medida_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_w 	varchar(30) := '';


BEGIN

if (nr_unidade_medida_p > 0) then

	if (nr_unidade_medida_p = 1) then --Unidade medida consumo
		select	cd_unidade_medida_consumo
		into STRICT	cd_unidade_medida_w
		from 	material
                where 	cd_material = cd_material_p;

	elsif (nr_unidade_medida_p = 2) then	--Unidade medida em ML
		select	max(cd_unidade_medida)
		into STRICT	cd_unidade_medida_w
		from 	material_conversao_unidade
		where 	cd_material = cd_material_p
		and 	upper(cd_unidade_medida) = upper(obter_unid_med_usua('ML'));

	elsif (nr_unidade_medida_p = 3) then	--Unidade medida com menor prioridade
		select	cd_unidade_medida
		into STRICT	cd_unidade_medida_w
		from 	unidade_medida_dose_v
		where 	cd_material = cd_material_p
		order by ie_prioridade LIMIT 1;

	end if;
end if;

return	cd_unidade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
