CREATE OR REPLACE FUNCTION obter_um_rep_dispositivo ( ie_bomba_infusao_p text, ie_agrupador_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_dosagem_w	varchar(3);
ie_tipo_item_w		smallint;


BEGIN

if (ie_agrupador_p = 4) then
	ie_tipo_item_w := 1;
elsif (ie_agrupador_p = 1) then
	ie_tipo_item_w := 2;
end if;

if (ie_bomba_infusao_p IS NOT NULL AND ie_bomba_infusao_p::text <> '') then
	select	max(ie_tipo_dosagem)
	into STRICT	ie_tipo_dosagem_w
	from	rep_dispositivo
	where	ie_bomba_infusao	= ie_bomba_infusao_p
	and	ie_tipo_item		= ie_tipo_item_w;
end if;

return	ie_tipo_dosagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
