CREATE OR REPLACE FUNCTION obter_und_medida_operacao (cd_estabelecimento_p bigint, cd_material_p bigint, nr_seq_operacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_unidade_medida_w	varchar(10);
cd_unicade_medida_w	varchar(30);


BEGIN

if (coalesce(cd_material_p,0) > 0) then

	select	coalesce(max(ie_unidade_medida),'C')
	into STRICT	ie_unidade_medida_w
	from	operacao_terceiro
	where	nr_sequencia	= nr_seq_operacao_p;

	select	CASE WHEN ie_unidade_medida_w='S' THEN cd_unidade_medida_compra WHEN ie_unidade_medida_w='E' THEN cd_unidade_medida_estoque  ELSE cd_unidade_medida_consumo END
	into STRICT	cd_unicade_medida_w
	from	material
	where	cd_material	= cd_material_p;

end if;

return	cd_unicade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
