CREATE OR REPLACE FUNCTION obter_unidades_setor_dt (cd_setor_atendimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_unidades_setor_w	smallint;


BEGIN

SELECT	COUNT(*) - SUM(CASE WHEN b.ie_temporario='S' THEN  1  ELSE 0 END )
INTO STRICT	qt_unidades_setor_w
FROM	setor_atendimento c,
     	unidade_atendimento b
WHERE 	b.cd_setor_atendimento     	= c.cd_setor_atendimento
AND	obter_se_unidade_ativa_data(dt_referencia_p,B.NR_SEQ_INTERNO)              	= 'A'
AND	obter_status_unidade_data(dt_referencia_p,B.NR_SEQ_INTERNO)              	<> 'I'
AND	c.cd_classif_setor 		IN (3,4)
AND	c.cd_setor_atendimento 		= cd_setor_atendimento_p
AND	b.dt_criacao			<= dt_referencia_p;
RETURN	qt_unidades_setor_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
