CREATE OR REPLACE FUNCTION obter_unidade_atendimento_date ( nr_atendimento_p bigint, ie_opcao_p text, ie_informacao_p text) RETURNS timestamp AS $body$
DECLARE

 
/* Opcao							*/
 
/* 	A = Unidade Atual 					*/
 
 
/* Informacao						 
	DTR = Data entrada real 
*/
 
 
dt_unidade_w			timestamp;
nr_seq_interno_w		bigint;


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
	begin 
	nr_seq_interno_w	:= obter_atepacu_paciente(nr_atendimento_p, ie_opcao_p);
	if (ie_informacao_p = 'DTR') then 
		begin 
		select 	a.dt_entrada_real 
		into STRICT	dt_unidade_w 
		from	atend_paciente_unidade a 
		where	a.nr_seq_interno = nr_seq_interno_w;
		exception 
			when others then 
				dt_unidade_w	:= '';
		end;
	end if;
	end;
end if;
	 
return dt_unidade_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
