SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_unidade_atend_data ( nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE



cd_setor_atendimento_w		bigint;
nr_seq_interno_w		bigint;
cd_unidade_basica_w		varchar(010);
cd_unidade_compl_w		varchar(010);
ds_retorno_w			varchar(255);


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	begin

	select	coalesce(max(nr_seq_interno),0)
	into STRICT	nr_seq_interno_w
	from 	atend_paciente_unidade a
	where	a.nr_atendimento 		= nr_atendimento_p
	  and 	nr_seq_interno	=
		(SELECT max(coalesce(nr_seq_interno,0))
		from 	Setor_Atendimento y,
			atend_paciente_unidade b
		where nr_atendimento 	= nr_atendimento_p
		  and 	b.cd_setor_atendimento	= y.cd_setor_atendimento
		  and	dt_referencia_p between  b.dt_entrada_unidade  and coalesce(b.dt_saida_unidade,dt_referencia_p)
		  and 	y.cd_classif_setor in (1,3,4,8)); /* alterado por marcus em 16/4/2005  inclui 1 */
	if (coalesce(nr_seq_interno_w,0)	> 0) then
		begin
		select 	a.cd_unidade_basica,
			a.cd_unidade_compl
		into STRICT	cd_unidade_basica_w,
			cd_unidade_compl_w
		from	atend_paciente_unidade a
		where	a.nr_seq_interno = nr_seq_interno_w;
		exception
			when others then
				cd_unidade_basica_w	:= '';
				cd_unidade_compl_w	:= '';
		end;
		ds_retorno_w	:= cd_unidade_basica_w || ' ' || cd_unidade_compl_w;
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
