CREATE OR REPLACE FUNCTION obter_unidade_cirurgia_pepo ( nr_seq_pepo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_interno_w	bigint;
nr_cirurgia_w		bigint;


BEGIN
if (nr_seq_pepo_p > 0) then
	begin
	select	min(nr_cirurgia)
	into STRICT	nr_cirurgia_w
	from	cirurgia
	where	nr_seq_pepo = nr_seq_pepo_p
	and 	(dt_entrada_unidade IS NOT NULL AND dt_entrada_unidade::text <> '');

	nr_seq_interno_w := obter_unidade_cirurgia(nr_cirurgia_w);
	end;
end if;

return nr_seq_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
