CREATE OR REPLACE FUNCTION obter_unidade_cirurgia ( nr_cirurgia_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_seq_interno_w	bigint;
dt_entrada_unidade_w	timestamp;
nr_atendimento_w	bigint;



BEGIN

select	dt_entrada_unidade,
	nr_atendimento
into STRICT	dt_entrada_unidade_w,
	nr_atendimento_w
from	cirurgia
where	nr_cirurgia	=	nr_cirurgia_p;

if	(nr_atendimento_w > 0 AND dt_entrada_unidade_w IS NOT NULL AND dt_entrada_unidade_w::text <> '') then
	select	coalesce(max(b.nr_seq_interno),0)
	into STRICT	nr_seq_interno_w
	from	unidade_atendimento b,
		atend_paciente_unidade a
	where	a.cd_setor_atendimento	=	b.cd_setor_atendimento
	and	a.cd_unidade_basica	=	b.cd_unidade_basica
	and	a.cd_unidade_compl	=	b.cd_unidade_compl
	and	a.nr_atendimento	=	nr_atendimento_w
	and	a.dt_entrada_unidade	=	dt_entrada_unidade_w;
end if;

return	nr_seq_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
