CREATE OR REPLACE FUNCTION obter_unidade_medida_ipe ( nr_seq_item_p bigint, ie_tipo_atendimento_p bigint, ie_tipo_item_p bigint, cd_item_p bigint, cd_cgc_hospital_p text, cd_unidade_medida_p text) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_w	w_interf_conta_item.cd_unidade_medida%type;
ie_origem_preco_w	material_atend_paciente.ie_origem_preco%type;
cd_convenio_w		material_atend_paciente.cd_convenio%type;
nr_interno_conta_w		material_atend_paciente.nr_interno_conta%type;
tp_nota_w		smallint;


BEGIN

cd_unidade_medida_w	:= null;

select	max(ie_origem_preco),
	max(cd_convenio),
	max(nr_interno_conta)
into STRICT	ie_origem_preco_w,
	cd_convenio_w,
	nr_interno_conta_w
from	material_atend_paciente
where	nr_sequencia = nr_seq_item_p
and	cd_material = cd_item_p;

if (ie_origem_preco_w IS NOT NULL AND ie_origem_preco_w::text <> '') then

	select	max(obter_tipo_nota_ipe(ie_tipo_atendimento_p, ie_tipo_item_p, cd_item_p, cd_cgc_hospital_p,0, nr_interno_conta_w))
	into STRICT	tp_nota_w
	;

	if (tp_nota_w in (76, 86)) then
		if (obter_se_somente_numero(cd_unidade_medida_p) = 'S') then
			cd_unidade_medida_w	:= cd_unidade_medida_p;
		else
			select	max(cd_unidade_convenio)
			into STRICT	cd_unidade_medida_w
			from	conversao_unidade_medida
			where	cd_unidade_medida = cd_unidade_medida_p
			and 	cd_convenio = cd_convenio_w;
		end if;
	end if;

end if;

return cd_unidade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
