CREATE OR REPLACE FUNCTION obter_unidade_medida_lactario ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(5);



BEGIN

	SELECT 	max(cd_unidade_medida)
	INTO STRICT	ds_retorno_w
	FROM	nutricao_leite_deriv
	WHERE	cd_material = cd_material_p
	and 	coalesce(ie_situacao,'A') = 'A';


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
