CREATE OR REPLACE FUNCTION obter_unidade_medida_mat ( cd_unidade_medida_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_unidade_medida_w	varchar(40);


BEGIN
if (cd_unidade_medida_p IS NOT NULL AND cd_unidade_medida_p::text <> '') then
	select	max(ds_unidade_medida)
	into STRICT	ds_unidade_medida_w
	from	unidade_medida_dose_v
	where	cd_unidade_medida	= cd_unidade_medida_p
	and	cd_material		= cd_material_p;
end if;

RETURN	ds_unidade_medida_w;

END	;
$body$
