SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_unidade_medida_prior ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_w 		varchar(30);
ie_prioridade_conversao_w	varchar(2);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select 	coalesce(max(ie_prioridade_conversao),'MA')
	into STRICT	ie_prioridade_conversao_w
	from	parametros_quimio
	where 	1 = 1;
	if (ie_prioridade_conversao_w = 'MA') then
		select  max(a.cd_unidade_medida)
		into STRICT 	cd_unidade_medida_w
		from 	material_conversao_unidade a
		where 	a.cd_material = cd_material_p
		and  	a.ie_prioridade = 	(SELECT max(b.ie_prioridade)
						from 	material_conversao_unidade b
						where 	b.cd_material = cd_material_p)
						order by a.ie_prioridade;
	else
			select  max(a.cd_unidade_medida)
			into STRICT 	cd_unidade_medida_w
			from 	material_conversao_unidade a
			where 	a.cd_material = cd_material_p
			and  	a.ie_prioridade = 	(SELECT min(b.ie_prioridade)
							from 	material_conversao_unidade b
							where 	b.cd_material = cd_material_p)
							order by a.ie_prioridade;
	end if;

end if;

return cd_unidade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
