CREATE OR REPLACE FUNCTION obter_unid_atend_medic_autor ( nr_seq_autorizacao_p bigint, cd_material_p bigint, nr_ciclo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w		bigint;
cd_unid_med_dose_w	varchar(30);
nr_seq_atendimento_w	bigint;
nr_seq_paciente_setor_w	bigint;

BEGIN

select	max(nr_seq_paciente_setor)
into STRICT	nr_seq_paciente_setor_w
from	autorizacao_convenio
where	nr_sequencia	= nr_seq_autorizacao_p
and	nr_ciclo	= nr_ciclo_p;

select	max(coalesce(CD_UNID_MED_PRESCR,cd_unid_med_dose))
into STRICT	cd_unid_med_dose_w
from	paciente_atend_medic a,
	paciente_atendimento b
where	a.nr_seq_atendimento	= b.nr_seq_atendimento
and	b.nr_seq_paciente	= nr_seq_paciente_setor_w
and	a.cd_material		= cd_material_p;

return	cd_unid_med_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
