CREATE OR REPLACE FUNCTION obter_unid_conversao_matpaci ( nr_seq_matpaci_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_w	varchar(30);


BEGIN

begin

select	coalesce(max(cd_unidade_medida),' ')
into STRICT	cd_unidade_medida_w
from	mat_atend_pac_convenio
where	nr_seq_material = nr_seq_matpaci_p;

exception
when others then
	cd_unidade_medida_w:= '';
end;

return	cd_unidade_medida_w;

end;
$body$
