CREATE OR REPLACE FUNCTION obter_unid_medida_mat ( cd_estabelecimento_p bigint, cd_cnpj_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_unid_medida_w		varchar(30);


BEGIN 
 
if (coalesce(cd_cnpj_p::text, '') = '') then 
	 
	select	coalesce(max(cd_unid_medida),0) 
	into STRICT	cd_unid_medida_w 
	from	material 
	where	cd_material = cd_material_p;
 
else 
	select	obter_dados_material_fornec(cd_estabelecimento_p, cd_cnpj_p, cd_material_p, 0) 
	into STRICT	cd_unid_medida_w 
	;
	 
	if (coalesce(cd_unid_medida_w::text, '') = '') then 
	 
		select	coalesce(max(cd_unid_medida),0) 
		into STRICT	cd_unid_medida_w 
		from	material 
		where	cd_material = cd_material_p;
	end if;
end if;
 
return	cd_unid_medida_w;
 
end;
