CREATE OR REPLACE FUNCTION obter_unid_med_anv (cd_unidade_medida_p text) RETURNS varchar AS $body$
DECLARE


ie_unidade_med_inter_w	varchar(30);


BEGIN

select	max(ie_unidade_med_inter)
into STRICT	ie_unidade_med_inter_w
from 	unidade_medida
where  	upper(cd_unidade_medida)  = upper(cd_unidade_medida_p);


