CREATE OR REPLACE FUNCTION obter_unid_med_seq_serv ( nr_sequencia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_convenio_w		integer;
cd_categoria_w		varchar(10);
cd_estabelecimento_w	smallint;
ds_retorno_w		varchar(10);

 

BEGIN 
 
begin 
 
begin 
 
select	cd_convenio, 
	cd_categoria, 
	obter_estab_atend(nr_atendimento) 
into STRICT	cd_convenio_w, 
	cd_categoria_w, 
	cd_estabelecimento_w 
from	procedimento_paciente 
where	nr_sequencia	= nr_sequencia_p;
exception 
	when others then 
	cd_convenio_w		:= 0;
	cd_categoria_w		:= '0';
	cd_estabelecimento_w	:= 1;
end;
 
select	substr(Obter_Unid_Med_Servico(cd_convenio_w, cd_categoria_w, cd_procedimento_p, ie_origem_proced_p, cd_estabelecimento_w),1,10) 
into STRICT	ds_retorno_w
;
exception 
	when others then 
	ds_retorno_w	:= 'Un';
end;
 
return	ds_retorno_w;
 
END;
$body$
