CREATE OR REPLACE FUNCTION obter_urinario_barthel (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

select 	CASE WHEN IE_URINARIO=0 THEN 'Incontinente ou cateterizado e incapaz de urinar sozinho' WHEN IE_URINARIO=5 THEN 'Ocasionalmente há micção acidental' WHEN IE_URINARIO=10 THEN 'Continência' END
into STRICT	ds_retorno_w
from	escala_barthel
where	nr_sequencia = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
