CREATE OR REPLACE FUNCTION obter_usuarios_grupos ( ds_grupos_p text) RETURNS varchar AS $body$
DECLARE


ds_usuario_w	varchar(2000);
nm_usuario_w	varchar(15);

c01 CURSOR FOR
	SELECT	nm_usuario_grupo
	from	grupo_usuario a,
		usuario_grupo b
	where	b.nr_seq_grupo = a.nr_sequencia
	and	obter_se_contido_char(ds_grupo,ds_grupos_p) = 'S';


BEGIN

open C01;
loop
fetch C01 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_usuario_w::text, '') = '' ) then
		ds_usuario_w	:= nm_usuario_w;
	else
		ds_usuario_w	:= ds_usuario_w || ',' || nm_usuario_w;
	end if;
	end;
end loop;
close C01;

return	ds_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
