CREATE OR REPLACE FUNCTION obter_usuarios_ocultos_comunic (nr_sequencia_p bigint, ds_usuarios_ocultos_p text, nm_usuario_comunic_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_usuarios_ocultos_w	varchar(2000)	:= null;
nm_usuario_comunic_w		varchar(15);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (coalesce(ds_usuarios_ocultos_p::text, '') = '') or (coalesce(nm_usuario_comunic_p::text, '') = '') then
		select	nm_usuario,
			ds_usuarios_ocultos
		into STRICT	nm_usuario_comunic_w,
			ds_usuarios_ocultos_w
		from	comunic_interna
		where	nr_sequencia	= nr_sequencia_p;
	else
		ds_usuarios_ocultos_w	:= ds_usuarios_ocultos_p;
		nm_usuario_comunic_w	:= nm_usuario_comunic_p;
	end if;

	if (nm_usuario_comunic_w <> nm_usuario_p) then
		ds_usuarios_ocultos_w	:= null;
	end if;

end if;

return	ds_usuarios_ocultos_w;

end;
$body$
