CREATE OR REPLACE FUNCTION obter_usuario_ativo_pf ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_w		varchar(15);


BEGIN

if (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin
	select	max(nm_usuario)
	into STRICT	nm_usuario_w
	from	usuario
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_situacao = 'A';
	end;
end if;

return	nm_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
