SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_usuario_atualiza_pf ( Cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_pf_w		varchar(15);
nm_usuario_compl_w	varchar(15);
dt_atualizacao_pf_W	timestamp;
dt_atualizacao_compl_W	timestamp;


BEGIN

select	max(dt_atualizacao),
	nm_usuario
into STRICT	dt_atualizacao_pf_W,
	nm_usuario_pf_w
from 	pessoa_fisica
where	cd_pessoa_fisica = Cd_pessoa_fisica_p
and     dt_atualizacao  = (	SELECT	max(dt_atualizacao)
                           	from	pessoa_fisica
                           	where	cd_pessoa_fisica = cd_pessoa_fisica_P)
group by nm_usuario;

select	max(dt_atualizacao),
	nm_usuario
into STRICT	dt_atualizacao_compl_W,
	nm_usuario_compl_w
from 	compl_pessoa_fisica
where	cd_pessoa_fisica = Cd_pessoa_fisica_p
and     dt_atualizacao  = (	SELECT	max(dt_atualizacao)
                           	from	compl_pessoa_fisica
                           	where	cd_pessoa_fisica = cd_pessoa_fisica_P)
group by nm_usuario;

if (dt_atualizacao_pf_W < dt_atualizacao_compl_W) then
	return nm_usuario_compl_w;
else
	return nm_usuario_pf_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
