CREATE OR REPLACE FUNCTION obter_usuario_autorizador (nr_sequencia_autor_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_autor_w	varchar(15);


BEGIN

select	max(a.nm_usuario)
into STRICT	nm_usuario_autor_w
from	autorizacao_convenio_hist a,
	estagio_autorizacao b
where	a.nr_seq_estagio = b.nr_sequencia
and	a.nr_sequencia_autor = nr_sequencia_autor_p
and	b.ie_interno = '10';


return	nm_usuario_autor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
