CREATE OR REPLACE FUNCTION obter_usuario_conta_hist (nr_interno_conta_p bigint, nr_nivel_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_w		varchar(15);
ds_retorno_w		varchar(15):= Null;

C01 CURSOR FOR
	SELECT	nm_usuario
	from	conta_paciente_hist
	where	nr_interno_conta = nr_interno_conta_p
	and 	nr_nivel_atual = nr_nivel_p
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w:= nm_usuario_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
