CREATE OR REPLACE FUNCTION obter_usuario_deleg_proc_aprov ( nr_sequencia_p bigint, nr_seq_proc_aprov_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_usuario_w		varchar(60);
ds_Resultado_w	 	varchar(2000);
cd_pessoa_fisica_w	varchar(10);
nm_usuario_deleg_w	varchar(15);

c01 CURSOR FOR 
SELECT	nm_usuario 
from	pessoas_processo_aprovacao_v 
where	nr_sequencia = nr_sequencia_p 
and	nr_seq_proc_aprov	= nr_seq_proc_aprov_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	obter_pessoa_fisica_usuario(nm_usuario_w,'C') 
	into STRICT	cd_pessoa_fisica_w 
	;
		 
	select	obter_usuario_pessoa(obter_pessoa_delegacao(cd_pessoa_fisica_w, 'AC', clock_timestamp())) 
	into STRICT	nm_usuario_deleg_w 
	;
	 
	if (nm_usuario_deleg_w IS NOT NULL AND nm_usuario_deleg_w::text <> '') then 
		begin 
		if (coalesce(ds_Resultado_w::text, '') = '') then 
			ds_Resultado_w	:= nm_usuario_deleg_w;
		else 
			ds_Resultado_w	:= ds_Resultado_w || ', ' || nm_usuario_deleg_w;
		end if;
		end;
	end if;	
	end;
end loop;
close C01;
 
RETURN ds_Resultado_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
