CREATE OR REPLACE FUNCTION obter_usuario_executor ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nm_exec_w	varchar(15);

C01 CURSOR FOR
	SELECT	NM_USUARIO_EXEC
	from	man_ordem_servico_exec
	where	nr_seq_ordem = nr_sequencia_p;


BEGIN

open C01;
	loop
	fetch C01 into
		nm_exec_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_retorno_w := ds_retorno_w || nm_exec_w || ', ';
	end loop;
close C01;

return substr(ds_retorno_w,1,(length(ds_retorno_w) - 2));

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
