CREATE OR REPLACE FUNCTION obter_usuario_lab_interf ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_usuario_w		bigint := 0;
ds_retorno_w		varchar(30) := '';


BEGIN

select  count(*)
into STRICT	qt_usuario_w
from	usuario
where	upper(nm_usuario) =  upper(nm_usuario_p);

if (qt_usuario_w > 0) then

	ds_retorno_w :=	 nm_usuario_p;

else
	select	max(nm_usuario)
	into STRICT	ds_retorno_w
	from	usuario
	where	upper(ds_login) = upper(nm_usuario_p);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nm_usuario_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
