CREATE OR REPLACE FUNCTION obter_usuario_lib_aprov_pag ( nr_bordero_p bigint, nr_titulo_p bigint, nr_seq_banco_escrit_p bigint) RETURNS varchar AS $body$
DECLARE

						
nm_usuario_liberacao_w	varchar(200);


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	begin
	select max(coalesce(OBTER_NOME_USUARIO(a.NM_USUARIO_LIB),a.NM_USUARIO_LIB))
	into STRICT nm_usuario_liberacao_w
	from conta_pagar_lib a
	where	nr_titulo	= nr_titulo_p
	and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and a.dt_liberacao = (SELECT max(x.dt_liberacao)
                          from conta_pagar_lib x
                          where	nr_titulo	= a.nr_titulo
                        and (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> ''));
	end;
elsif (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then	
	begin
	select max(coalesce(OBTER_NOME_USUARIO(a.NM_USUARIO_LIB),a.NM_USUARIO_LIB))
	into STRICT nm_usuario_liberacao_w
	from conta_pagar_lib a
	where	nr_bordero	= nr_bordero_p
	and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and a.dt_liberacao = (SELECT max(x.dt_liberacao)
                          from conta_pagar_lib x
                          where	nr_bordero	= a.nr_bordero
                        and (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> ''));
	end;
elsif (nr_seq_banco_escrit_p IS NOT NULL AND nr_seq_banco_escrit_p::text <> '') then	
	begin
	select max(coalesce(OBTER_NOME_USUARIO(a.NM_USUARIO_LIB),a.NM_USUARIO_LIB))
	into STRICT nm_usuario_liberacao_w
	from conta_pagar_lib a
	where	nr_seq_banco_escrit  = nr_seq_banco_escrit_p
	and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and a.dt_liberacao = (SELECT max(x.dt_liberacao)
                          from conta_pagar_lib x
                          where	nr_seq_banco_escrit	= a.nr_seq_banco_escrit
                        and (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> ''));
	end;
end if;

return nm_usuario_liberacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
