CREATE OR REPLACE FUNCTION obter_usuario_pf_ci (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) := '';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select 	substr(coalesce(nm_usuario_princ_ci,'0'),1,15)
	into STRICT	ds_retorno_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ds_retorno_w = '0') then
		select	substr(obter_usuario_pf(cd_pessoa_fisica_p),1,15)
		into STRICT	ds_retorno_w
		;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
