CREATE OR REPLACE FUNCTION obter_usuario_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_w	varchar(15);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	/* obter usu√°rio pf */

	select	max(nm_usuario)
	into STRICT	nm_usuario_w
	from	usuario
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_situacao = 'A';
end if;

return nm_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
