CREATE OR REPLACE FUNCTION obter_usuario_prescritor ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_prescritor_w		pessoa_fisica.cd_pessoa_fisica%type;
ie_medico_w			char(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	coalesce(max(cd_prescritor),max(cd_medico))
	into STRICT	cd_prescritor_w
	from	prescr_medica where		nr_prescricao	= nr_prescricao_p LIMIT 1;

	select	coalesce(max('S'),'N')
	into STRICT	ie_medico_w
	from	medico where		cd_pessoa_fisica	= cd_prescritor_w
	and		coalesce(ie_situacao,'A')		= 'A' LIMIT 1;

	if (ie_medico_w = 'S') then
		return	substr(obter_nome_medico(cd_prescritor_w, 'PC'),1,200);
	else	
		return	substr(obter_nome_pf(cd_prescritor_w),1,60) || ' (' || substr(obter_ds_cod_prof(cd_prescritor_w),1,30) || ')';
	end if;
end if;

return	'';

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
