CREATE OR REPLACE FUNCTION obter_usuario_progr_proj (nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_progr_w	bigint;
nm_usuario_w	varchar(15);


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	begin
	select	coalesce(max(y.nr_sequencia),0)
	into STRICT	nr_seq_progr_w
	from 	proj_equipe x,
		proj_equipe_papel y
	where 	x.nr_seq_proj = nr_seq_projeto_p
	and	x.nr_sequencia = y.nr_seq_equipe
	and   	y.nr_seq_funcao = 44
	and	(y.cd_pessoa_fisica IS NOT NULL AND y.cd_pessoa_fisica::text <> '')
	and	coalesce(y.ie_situacao,'A') = 'A';

	if (nr_seq_progr_w > 0) then
		begin
		select	max(obter_usuario_pf(cd_pessoa_fisica))
		into STRICT	nm_usuario_w
		from	proj_equipe_papel
		where	nr_sequencia = nr_seq_progr_w;
		end;
	end if;
	end;
end if;
return nm_usuario_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
