CREATE OR REPLACE FUNCTION obter_usuario_restrito (nr_seq_registro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	ehr_template.ie_usuario_restrito%type;


BEGIN

select	max(coalesce(t.ie_usuario_restrito, 'S'))
into STRICT	ds_retorno_w
from	ehr_template t,
	ehr_reg_template e,
	ehr_registro g
where	t.nr_sequencia	= e.nr_seq_template
and	e.nr_seq_reg	= g.nr_sequencia
and	coalesce(g.dt_liberacao::text, '') = ''
and	g.nr_sequencia	= nr_seq_registro_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
