CREATE OR REPLACE FUNCTION obter_usuario_senha_integr ( nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(30) := null;


BEGIN

-- ie_opcao_p
-- U = Usu√°rio
-- S = Senha
-- I = Instituto
if (ie_opcao_p = 'U') then

	select  max(nm_usuario_integr)
	into STRICT	ds_retorno_w
	from 	lab_regra_usuario_integr
	where 	nm_usuario_tasy = nm_usuario_p;

	if (coalesce(ds_retorno_w::text, '') = '') then

		select  max(nm_usuario_integr)
		into STRICT	ds_retorno_w
		from 	lab_regra_usuario_integr
		where 	coalesce(nm_usuario_tasy::text, '') = '';

	end if;


elsif (ie_opcao_p = 'S') then

	select  max(ds_senha_usuario_integr)
	into STRICT	ds_retorno_w
	from 	lab_regra_usuario_integr
	where 	nm_usuario_tasy = nm_usuario_p;

	if (coalesce(ds_retorno_w::text, '') = '') then

		select  max(ds_senha_usuario_integr)
		into STRICT	ds_retorno_w
		from 	lab_regra_usuario_integr
		where 	coalesce(nm_usuario_tasy::text, '') = '';

	end if;


elsif (ie_opcao_p = 'I') then

	select  max(nm_instituto)
	into STRICT	ds_retorno_w
	from 	lab_regra_usuario_integr
	where 	nm_usuario_tasy = nm_usuario_p;

	if (coalesce(ds_retorno_w::text, '') = '') then

		select  max(nm_instituto)
		into STRICT	ds_retorno_w
		from 	lab_regra_usuario_integr
		where 	coalesce(nm_usuario_tasy::text, '') = '';

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
