CREATE OR REPLACE FUNCTION obter_usuario_tabela_assinada ( nr_seq_assinatura_p bigint, ie_processo_assinado_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_proj_ass_w	bigint;
nm_usuario_original_w	varchar(15);
nm_usuario_lib_enf_w	varchar(15);
nm_usuario_lib_farmac_w	varchar(15);
nm_usuario_w		varchar(15);


BEGIN

select	nr_seq_proj_ass,
	nm_usuario
into STRICT	nr_seq_proj_ass_w,
	nm_usuario_w
from	tasy_assinatura_digital
where	nr_sequencia = nr_seq_assinatura_p;

if (nr_seq_proj_ass_w = 1) then
	select	nm_usuario_original,
		nm_usuario_lib_enf,
		substr(obter_usuario_pf(cd_farmac_lib),1,15)
	into STRICT	nm_usuario_original_w,
		nm_usuario_lib_enf_w,
		nm_usuario_lib_farmac_w
	from	prescr_medica
	where	nr_seq_assinatura = nr_seq_assinatura_p;
end if;

if (ie_processo_assinado_p = 'PM') then
	return nm_usuario_original_w;
elsif (ie_processo_assinado_p = 'LPF') then
	return nm_usuario_lib_farmac_w;
elsif (ie_processo_assinado_p IS NOT NULL AND ie_processo_assinado_p::text <> '') then
	return nm_usuario_lib_enf_w;
else
	return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
