CREATE OR REPLACE FUNCTION obter_usu_alto_nivel (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_lib_max_w	varchar(15);
ie_nivel_max_w		bigint;
nm_usuario_lib_min_w	varchar(15);
ie_nivel_min_w		bigint;
nm_usuario_retorno_w	varchar(15);

C01 CURSOR FOR
	SELECT  a.nm_usuario_lib,
		coalesce(a.ie_nivel,0)
	from    conta_pagar_lib a
	where   (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and     (a.ie_nivel IS NOT NULL AND a.ie_nivel::text <> '')
	and     a.nr_titulo    = nr_titulo_p
	order by	a.ie_nivel;


BEGIN

nm_usuario_retorno_w	:= '';

open C01;
loop
fetch C01 into
	nm_usuario_lib_max_w,
	ie_nivel_max_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nm_usuario_retorno_w	:= '';
	end;
end loop;
close C01;

select  max(a.nm_usuario_lib),
	coalesce(max(a.ie_nivel),0)
into STRICT	nm_usuario_lib_min_w,
	ie_nivel_min_w
from    conta_pagar_lib a
where   (a.ie_nivel IS NOT NULL AND a.ie_nivel::text <> '')
and     a.nr_titulo    = nr_titulo_p;

if (ie_nivel_max_w = ie_nivel_min_w) then
	nm_usuario_retorno_w	:= nm_usuario_lib_max_w;
end if;

return	nm_usuario_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
