SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_usu_comunic_compra ( nr_sequencia_p bigint, ie_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1);
cd_evento_w		integer;

c01 CURSOR FOR
SELECT	cd_evento
from	regra_envio_comunic_evento
where	nr_sequencia = nr_sequencia_p;


BEGIN

ie_permite_w	:= 'N';

OPEN C01;
LOOP
FETCH C01 into
	cd_evento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ie_permite_w = 'N') then
		begin
		if (cd_evento_w in (1,16)) and /*Aprovacao - Aprovar  processo*/
			(ie_usuario_p in ('SSC','UDS','UCO','SPC','UDC','CPC','RSC','SOC','CRS','UOI','USI','ULS','GPI','ARR','ARS')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w = 2) and /*Aprovacao - Reprovar processo*/
			(ie_usuario_p in ('SSC','UDS','UCO','SPC','UDC','CPC','RSC','SOC','CRS','UOI','USI','UPC','ULS','GPI','ARS','ARR')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (5)) and /*Cotacao*/
			(ie_usuario_p in ('SSC','UDS','CRS','UDC','RMM')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (3,4,8,10,28,47)) and /*Solicitacao*/
			(ie_usuario_p in ('SSC','UDS','CRS','UDC','RSC','SSS')) then			
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (3,4)) and /*Cotacao*/
			(ie_usuario_p in ('SSC','UDS','CRS','UDC','RSC')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w = 9) and /*Solicitacao - Ao liberar */
			(ie_usuario_p in ('SSC','UDS','CRS','UDC','RSC','ASC')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w = 6) and /*Solicitacao - Solicitar parecer*/
			(ie_usuario_p in ('URP','SPR')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w = 7) and /*Solicitacao - resposta parecer*/
			(ie_usuario_p in ('USP','SPR')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (11,12)) and /*Aprovacao - Aprovar e Reprovar ordem com iten nao padronizados*/
			(ie_usuario_p in ('COM','UCO','AOC')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (13)) and /*Ordem - Ao cancelar uma ordem de compra ja aprovada*/
			(ie_usuario_p in ('AOC','SOC','SSO')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (14)) and /*Cotacao - Ao ajustar quantidade fornecedor*/
			(ie_usuario_p in ('SSC','CRS','UDC','UDS')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (15)) and /*Solicitacao - Ao liberar uma solicitacao Urgente*/
			(ie_usuario_p in ('COM')) then
			ie_permite_w	:= 'S';
		elsif (cd_evento_w in (17)) and /*Cotacao - Avisar cotacao pendente*/
			(ie_usuario_p in ('CCC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (18,19)) and /*Pessoa Juridica - aprovacao da avaliacao fornecedor*/
			(ie_usuario_p in ('RAF','UCA')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (20)) and /*Cotacao - No momento do calculo*/
			(ie_usuario_p in ('CCC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (21)) and /*Ao Alterar ou Desdobrar as entrega dos itens*/
			(ie_usuario_p in ('UCO','SOC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (22)) and /*Ao Liberar Ordem de Compra */
			(ie_usuario_p in ('AOC', 'SOC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (27,36)) and /*Ao Liberar Ordem de Compra */
			(ie_usuario_p in ('URF')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (30,31,32,33,34)) and /*Controle transf estoques*/
			(ie_usuario_p in ('SOC','UCO')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (35)) and /*Avisa entrega item maior que prevista */
			(ie_usuario_p in ('SOC','UCO')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (37)) and /*Ordem - Itens nao entregue conforme previsto */
			(ie_usuario_p in ('SOC','UCO')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (49)) and /*Ordem - Ao cancelar entrega item */
			(ie_usuario_p in ('SOC','SSO')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (24)) and /*Requisicao - Ao liberar */
			(ie_usuario_p in ('ARE')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (51)) and /*Ordem - Ao baixar a ordem manualmente*/
			(ie_usuario_p in ('SOC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (52)) and /*Solicitacao - Ao devolver*/
			(ie_usuario_p in ('SSC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (53)) and /*Ordem - Ao liberar, e data limite de entrega superior a data informada na solicitacao*/
			(ie_usuario_p in ('SSC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (54)) and /*Inspecao - Ao gerar divergencia no momento da liberacao*/
			(ie_usuario_p in ('SOC','UCO')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (57)) and /*Solicitacao - Pendente de aprovacao*/
			(ie_usuario_p in ('SSC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (58)) and /*Ordem - Pendente de aprovacao*/
			(ie_usuario_p in ('SOC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (63)) and /*Cotacao - Ao gerar aprovadores */
			(ie_usuario_p in ('CAC','CCC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (65)) and /*Cotacao - Ao gerar aprovadores */
			(ie_usuario_p in ('ARS','ARR')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (67)) and /*Avisar pendencia de parecer da lista critica*/
			(ie_usuario_p in ('RPL')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (68)) and /*Avisar pendencia de nao conformidade curva ABC*/
			(ie_usuario_p in ('RNC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (69)) and /*Aprovacao - Ao excluir item*/
			(ie_usuario_p in ('SPR')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (71)) and /*Aprovacao - Ao aprovar uma cotacao de compra.*/
			(ie_usuario_p in ('SPR','CCC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (72)) and /*Aprovacao - Ao aprovar uma cotacao de compra*/
			(ie_usuario_p in ('SSC','CRS')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (73)) and /*Inspecao - Ao liberar a inspecao*/
			(ie_usuario_p in ('SSC')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (76)) and /*Cotacao - Ao gerar OC, com itens nao cotados*/
			(ie_usuario_p in ('SCC','CCC')) then
			ie_permite_w := 'S';	
		elsif (cd_evento_w in (81)) and /*Ordem - Ao aprovar uma ordem com adiantamento*/
			(ie_usuario_p in ('SOC','UCO')) then
			ie_permite_w := 'S';
		elsif (cd_evento_w in (82)) and /*Nota fiscal - Aviso de chegada (Ordem compra)*/
			(ie_usuario_p in ('SOC','UCO','RMM')) then
			ie_permite_w := 'S';	
		elsif (cd_evento_w in (84)) and /*Nota fiscal - Usuario responsavel pelo contrato*/
            (ie_usuario_p in ('URC')) then
            ie_permite_w := 'S';
		end if;
		end;
	end if;
end loop;
close c01;

return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_usu_comunic_compra ( nr_sequencia_p bigint, ie_usuario_p text) FROM PUBLIC;

