SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_usu_orig_assinatura2 ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_itens_w		bigint;
nm_usuario_original_w	varchar(15);
cd_medico_w		varchar(10);
qt_conta_w		bigint := 0;
nm_usuario_original_ww	varchar(15) := '0';
qt_lancamento_w		bigint;

C01 CURSOR FOR
	SELECT	sum(qt_itens) qt_itens,
		nm_usuario_original
	from (
		SELECT	count(*) qt_itens,
			nm_usuario_original
		from	material_atend_paciente a,
			estrutura_material_v b
		where	a.cd_material = b.cd_material
		and	b.cd_grupo_material in (13,16)
		and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
		and	nr_atendimento = nr_atendimento_p
		and	cd_setor_atendimento = 41
		group by	nm_usuario_original) alias4
	group by nm_usuario_original;


BEGIN

open C01;
loop
fetch C01 into
	qt_itens_w,
	nm_usuario_original_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		select	count(*)
		into STRICT	qt_lancamento_w
		from	usuario a,
			usuario_assinatura b
		where	a.nm_usuario = b.nm_usuario_atual
		and	b.nm_usuario_atual = nm_usuario_original_w;

		if (qt_lancamento_w > 0) then

			if (qt_itens_w > qt_conta_w) then
				nm_usuario_original_ww := nm_usuario_original_w;
			end if;

			qt_conta_w := qt_itens_w;

		end if;

	end;
end loop;
close C01;

select 	coalesce(max(cd_pessoa_fisica),'0')
into STRICT	cd_medico_w
from 	usuario
where	nm_usuario = nm_usuario_original_ww
and	ie_situacao = 'A';

return	cd_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
