SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_utc (dt_referencia_p timestamp default clock_timestamp()) RETURNS varchar AS $body$
DECLARE


ie_utc_w	varchar(15);


BEGIN

begin
	select	ie_utc
	into STRICT	ie_utc_w
	from	estab_horario_verao
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
	and	dt_referencia_p between dt_inicial and dt_final;

exception
when others then
	ie_utc_w	:= null;
end;

if (coalesce(ie_utc_w::text, '') = '') then
	begin
		select	max(coalesce(ie_utc,TO_CHAR(CURRENT_TIMESTAMP,'TZR')))
		into STRICT	ie_utc_w
		from	estabelecimento
		where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;	
	exception
	when others then
		ie_utc_w	:= TO_CHAR(CURRENT_TIMESTAMP,'TZR');
	end;
end	if;
	

return coalesce(ie_utc_w, TO_CHAR(CURRENT_TIMESTAMP,'TZR'));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
