CREATE OR REPLACE FUNCTION obter_utiliza_nom (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

				
ie_config_nom_w	bigint;
ie_nom_w		varchar(2) := 'N';


BEGIN

select	count(*)
into STRICT    ie_config_nom_w
from	config_nom
where	cd_estabelecimento = cd_estabelecimento_p;

if	((coalesce(ie_config_nom_w, 0) > 0) and (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'es_MX')) then
	begin
		ie_nom_w := 'S';
	end;
end if;

return	ie_nom_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
