SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_uuid_nf_canc_mx (nr_sequencia_nf_p bigint) RETURNS varchar AS $body$
DECLARE


nf_sequencia_w  nota_fiscal.nr_sequencia%type;
nr_nfe_imp_w    nota_fiscal.nr_nfe_imp%type;


BEGIN

  if (nr_sequencia_nf_p IS NOT NULL AND nr_sequencia_nf_p::text <> '') then

	select  	substr(obter_nf_conta(max(b.nr_seq_conta_origem),1),1,100)
	into STRICT   		nf_sequencia_w
	from    	nota_fiscal a,
			conta_paciente b
        where  		a.nr_interno_conta = b.nr_interno_conta
        and 		a.nr_sequencia = nr_sequencia_nf_p;

	if (nf_sequencia_w IS NOT NULL AND nf_sequencia_w::text <> '') then

		select max(a.nr_nfe_imp)
		into STRICT   nr_nfe_imp_w
		from   nota_fiscal a
		where  a.nr_sequencia = nf_sequencia_w;


	end if;

  end if;

return nr_nfe_imp_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
