CREATE OR REPLACE FUNCTION obter_validade_cpoe_md (dt_inicio_ww timestamp, dt_fim_ww timestamp ) RETURNS varchar AS $body$
DECLARE


  qt_horas_w varchar(10);

BEGIN
    qt_horas_w := trunc(dividir_sem_round_md(obter_dif_data(dt_inicio_ww, dt_fim_ww,'TM'),60),0)+1;

    if (qt_horas_w > 24 or coalesce(dt_fim_ww::text, '') = '') then
      return '24';
    else
      return qt_horas_w;
    end if;

END;
$body$
LANGUAGE PLPGSQL
