CREATE OR REPLACE FUNCTION obter_validade_senha_autor_pls (nr_seq_agenda_w bigint) RETURNS timestamp AS $body$
DECLARE

dt_validade_w		timestamp;
nr_seq_guia_plano_w	bigint;


BEGIN

begin
select	max(nr_seq_guia_plano)
into STRICT	nr_seq_guia_plano_w
from	autorizacao_convenio
where	nr_seq_agenda_consulta = nr_seq_agenda_w;
exception
when others then
	nr_seq_guia_plano_w := 0;
end;

if (coalesce(nr_seq_guia_plano_w,0)  > 0) then

	select	max(dt_validade_senha)
	into STRICT	dt_validade_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_plano_w;
end if;

return	dt_validade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
