CREATE OR REPLACE FUNCTION obter_valores_adiantamento ( nr_adiantamento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*

 CH	- Vl cheque
 CAC	- Vl cartão crédito
 CAD	- Vl cartão débito

*/
vl_cheques_w		double precision;
vl_cartao_cre_w		double precision;
vl_cartao_deb_w		double precision;
vl_retorno_w		double precision;


BEGIN

select	coalesce(sum(vl_cheque),0)
into STRICT	vl_cheques_w
from	cheque_cr
where	nr_adiantamento	= nr_adiantamento_p;

select	coalesce(sum(CASE WHEN b.ie_tipo_cartao='C' THEN b.vl_transacao END ),0) vl_cartao_cre,
	coalesce(sum(CASE WHEN b.ie_tipo_cartao='D' THEN b.vl_transacao END ),0) vl_cartao_deb
into STRICT	vl_cartao_cre_w,
	vl_cartao_deb_w
from	movto_cartao_cr b
where	b.nr_adiantamento = nr_adiantamento_p;

if (ie_opcao_p	= 'CH') then
	vl_retorno_w	:= vl_cheques_w;
elsif (ie_opcao_p	= 'CAC') then
	vl_retorno_w	:= vl_cartao_cre_w;
elsif (ie_opcao_p	= 'CAD') then
	vl_retorno_w	:= vl_cartao_deb_w;
end if;

return coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valores_adiantamento ( nr_adiantamento_p bigint, ie_opcao_p text) FROM PUBLIC;

