CREATE OR REPLACE FUNCTION obter_valores_baixa_conta (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w		double precision;
vl_recebido_w		double precision;
vl_glosa_w		double precision;


BEGIN 
 
select	sum(vl_recebido), 
	sum(vl_glosa) 
into STRICT	vl_recebido_w, 
	vl_glosa_w 
from	convenio_retorno_item b, 
	titulo_receber_liq a 
where	a.nr_seq_ret_item	= b.nr_sequencia 
and	b.nr_interno_conta	= nr_interno_conta_p;
 
if (ie_opcao_p = 'VL_RECEBIDO') then 
	vl_retorno_w	:= vl_recebido_w;
 
elsif (ie_opcao_p = 'VL_GLOSA') then 
	vl_retorno_w	:= vl_glosa_w;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
