SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valores_banco_periodo (nr_seq_conta_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


vl_periodo_w		double precision;

/*

ie_tipo_valor_p :
'C' - credita
'D' - debita

*/
BEGIN

select	coalesce(sum(a.vl_transacao),0)
into STRICT	vl_periodo_w
from	banco_saldo b,
	transacao_financeira c,
	movto_trans_financ a
where	a.dt_transacao between trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
and	c.nr_sequencia		= a.nr_seq_trans_financ
and	a.nr_seq_saldo_banco	= b.nr_sequencia
and	c.IE_BANCO		= ie_tipo_valor_p
and	b.nr_seq_conta		= nr_seq_conta_p;

return	vl_periodo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
